/* malloc macros and constants */
#ifndef INTERNAL_SIZE_T
# define INTERNAL_SIZE_T size_t
#endif

#define MALLOC_ALIGNMENT (2 * SIZE_SZ < __alignof__ (long double) \
                          ? __alignof__ (long double) : 2 * SIZE_SZ)

#define SIZE_SZ (sizeof (INTERNAL_SIZE_T))
#define MIN_CHUNK_SIZE	32
#define MALLOC_ALIGN_MASK (MALLOC_ALIGNMENT - 1)
#define MINSIZE  \
  (unsigned long)(((MIN_CHUNK_SIZE+MALLOC_ALIGN_MASK) & ~MALLOC_ALIGN_MASK))
#define request2size(req)                                         \
  (((req) + SIZE_SZ + MALLOC_ALIGN_MASK < MINSIZE)  ?             \
   MINSIZE :                                                      \
   ((req) + SIZE_SZ + MALLOC_ALIGN_MASK) & ~MALLOC_ALIGN_MASK)

/* macros and typedef */
typedef unsigned long long ull;

void print_banner(){
	printf("\033[1;31m");
	printf("%s", banner);
	printf("\n\033[0m\n\n");
}

void init(){
    alarm(60);
    setvbuf(stdin, 0LL, 2, 0LL);
    setvbuf(stdout, 0LL, 2, 0LL);
    setvbuf(stderr, 0LL, 2, 0LL);
	int seed = 0;
	int fd = open("/dev/urandom", O_RDONLY);
	read(fd, &seed, 4);
	srand(seed);
}

void correct(){
	printf("\033[1;32mCorrect! \xe2\x9c\x93\033[0m\n\n");
}

void wrong(){
	printf("\033[1;31mWrong \xe2\x9d\x8c\033[0m\n");
	printf("Give it one more try!\n");
	exit(0);
}

void * getHeapBase(){
	void * a = malloc(0x100);
	free(a);
	return a-16;
}

void * alignHeap(){
	ull currentBase = (ull)getHeapBase();
	if(currentBase & 0xfff){
		ull sizeNeeded = (((currentBase >> 12) << 12)+0x1000) - currentBase;
		malloc(sizeNeeded-16);
	}
	return getHeapBase();
}

void sectionA(){
	printf("\e[1m##################### SECTION A #####################\e[0m\n");
	for(int i = 1; i <= 3; i++){
		unsigned int sz = rand() % 100; 
		unsigned int ans = 0;
		printf("Q%d: I call malloc(%u),\nWhat is the actual chunk size, including chunk metadata?\n(e.g. 1337): ", i,  sz);
		scanf("%u", &ans);
		if(ans == request2size(sz)){
			correct();
		}
		else{
			wrong();
		}
	}
	printf("Congrats! You have completed \e[1m[SECTION A]\e[0m\n");
	printf("\e[1m################# END OF SECTION A ##################\e[0m\n\n");
}

void sectionB(){
	printf("\e[1m##################### SECTION B #####################\e[0m\n");

	// Q1
	printf("Heap base is current at: 0x%016llx\n", (ull)alignHeap());
	int szs[3];
	void * ptrs[3];
	/* setup chunks */
	for(int i = 0; i < 3; i++){
		szs[i] = (rand() % 0x100) + 0x100;
		ptrs[i] = malloc(szs[i]);
		printf("malloc(%d) = 0x%016llx\n", szs[i], (ull)ptrs[i]);
	}

	int qtype = rand()%2;
	if(!qtype){ /* type A */
		printf("\n");
		for(int i = 1; i <= 2; i++){
			int ind = rand()%3;
			ull ans = 0;
			printf("Q%d: What is the QWORD value at 0x%016llx?\n(e.g. 0xdeadbeef): ", i, (ull)(ptrs[ind]-0x8));
			scanf("%llx", &ans);
			if(ans == *(ull*)(ptrs[ind]-0x8)){
				correct();
			}
			else{
				wrong();
			}
		}
	}
	else{		/* type B */
		int free_idx = rand()%2;
		free(ptrs[free_idx]);
		printf("\e[4mfree(0x%016llx)\e[0m\n\n", (unsigned long long)ptrs[free_idx]);
		
		/* Q1 */
		int i = 1;
		int ind = rand()%3;
		ull ans = 0;
		printf("Q%d: What is the QWORD value at 0x%016llx?\n(e.g. 0xdeadbeef): ", i, (unsigned long long)(ptrs[ind]-0x8));
		scanf("%llx", &ans);
		if(ans == *(unsigned long long *)(ptrs[ind]-0x8)){
			correct();
		}
		else{
			wrong();
		}

		/* Q2 */
		ind = free_idx + 1;
		printf("Q%d: What is the QWORD value at 0x%016llx?\n(e.g. 0xdeadbeef): ", i, (unsigned long long)(ptrs[ind]-0x10));
		scanf("%llx", &ans);
		if(ans == *(unsigned long long *)(ptrs[ind]-0x10)){
			correct();
		}
		else{
			wrong();
		}
	}

	printf("Congrats! You have completed \e[1m[SECTION B]\e[0m\n");
	printf("\e[1m################# END OF SECTION B ##################\e[0m\n\n");
}

void flag(){
	char flag[100];
	int fd = open("./flag", O_RDONLY);
	int n = read(fd, flag, 99);
	flag[n] = '\x00';
	printf("Congrats! Here is your flag: %s", flag);
}


int main(){
	init();
	print_banner();
	printf("Welcome to Heap School!\nWe'll be starting class with a pop quiz!\n");
	printf("\033[1;31mNote:\033[0m This challenge is based on the \033[1;31mUbuntu 16.04 [64-bit]\033[0m ptmalloc allocator (\033[1;31mUbuntu GLIBC 2.23-0ubuntu11\033[0m)\n\n");

	sectionA();
	sectionB();

	flag();
}
